// Converts an input like:
// [{ json: { text: "```json\n{ \"text\": \"...\", \"Result\": \"Qualified\" }\n```" } }]
// into: [{ json: { text: "...", Result: "Qualified" } }]

return $input.all().map(item => {
  const raw = (item.json.text ?? '').trim();

  if (typeof raw !== 'string' || !raw) {
    return { json: {} };
  }

  // Remove leading ```json and trailing ``` fences
  const withoutFences = raw.replace(/^\s*```json\s*|\s*```\s*$/g, '').trim();

  // Extra safety: slice between first '{' and last '}'
  const start = withoutFences.indexOf('{');
  const end = withoutFences.lastIndexOf('}');
  if (start === -1 || end === -1) {
    throw new Error('No JSON object found in text');
  }

  const jsonStr = withoutFences.slice(start, end + 1);
  const parsed = JSON.parse(jsonStr);

  return { json: parsed };
});
